  width: 400px;
  height: 100%;
  overflow: auto;
  border-right: 2px solid #EAEAEA;
}

.chat__list-head {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  padding: 16px 24px;
  border-bottom: 2px solid #EAEAEA;
}

.chat__list-search {
  width: 100%;
}

