  content: "";
  position: absolute;
  right: 0;
  top: 1px;
  width: 24px;
  height: 24px;
  background-repeat: no-repeat;
  background-position: center;
  background-image: url("/136b1e7da1842d6b78face20776cb8d4.svg");
}

