  height: 55px;
  width: 100%;
  border-bottom: 2px solid #EAEAEA;
}

.chat__messages {
  height: calc(100% - 130px);
  overflow: auto;
}

.chat__message {
  height: 75px;
  border-top: 2px solid #EAEAEA;
}

